import './App.css';
import { useEffect, useState } from 'react';
import Fact from './Fact';
import Pagination from './Pagination';

function App() {

  const [ facts, setFacts] = useState({})
  const [ catFactArray, setCatFactArray ] = useState([])
  const [ pageI, setPageI ] = useState(1);
  const [ num, setNumFacts ] = useState(0)
  const [ arr, setArr] = useState([]);

  useEffect(() => {
    var array = [...catFactArray, facts]    
    setCatFactArray(array);
    fetch("https://catfact.ninja/fact")
    .then((res) => res.json())
    .then((data) => {
      setFacts(data.fact)});
  }, [num])

  var min;
  var max;
  useEffect(() => {
    var temp = catFactArray;
    min = 2 + ((pageI - 1) * 10);
    max = 12 + ((pageI - 1) * 10);
    var output = temp.slice(min, max);
    output = output.reverse();
    setArr(output);
  }, [pageI, catFactArray, num])

  useEffect(() => {
    if (num > 1 && (num - 1) % 10 == 0) {
      setPageI(pageI + 1);
    }
  }, [num])
  return (
    <div className = 'App-header'>
      <p className = 'title' >Cat Facts!</p> 
      <p id = 'currentFact'> {facts.fact} </p>
      <button id="getFact" onClick={() => setNumFacts(num + 1)}>Give me a fact</button>
      <div className = "pastFacts"> 
      <p> Past Facts!</p>
      {arr.map((e) => {
                let k = arr.indexOf(e)
                return <Fact data={e} key={k} catFactArray={catFactArray} setCatFactArray={setCatFactArray}></Fact>;
            })}
      </div>
      <div>
      <Pagination index={pageI} setIndex={setPageI} num={num}></Pagination>
      </div>
    </div>
  );
}
export default App;

