function Pagination(splice) {

    const { index, setIndex, num} = splice
    let left = num % 10;
    let part = (left > 0) ? 1 : 0;
    let max = Math.floor(num / 10) + part;
    let min = 1;
    return (
        <div className="buttons">
            <button className="b" onClick={() => setIndex((index - 1 >= min)? index - 1 : min)}>back</button>
            <button className="b" onClick={() => setIndex((index + 1 <= max) ? index + 1 : max)}>forward</button>
        </div>
    )
}
export default Pagination;
