import {useEffect, useState} from 'react';

export default function Fact(props) {
    const { data, facts, setArr} = props;
    function toggle() {

        let temp = [
            ...facts
        ]
        let index = temp.indexOf(data)
        temp[index] = {
            fact: data.fact,
            i: index
        }
        setArr(temp)
      }

    return (
            <div onLoad={toggle} className="catFact"> 
                <div>
                    <button>{data.fact}</button>    
                    </div>
            </div>
    )
}
