import './App.css';

import React from 'react';
import {useState, useEffect} from 'react';
import Pagination from './Pagination';

function CatFacts() {
  const [facts, setFacts] = useState([]);
  const [ num, setNum] = useState(1);
  const [ page, setPage] = useState(1);
  const [ catFactArray, setCatFactArray ] = useState([])

  var count = 0;
  const initial = facts => facts.slice(0, -1);

  const fetchData = async () => {
    const response = await fetch("https://catfact.ninja/fact");
    const data = await response.json();
    var array = [...facts, data.fact]
    setFacts(array);
    setNum(array.length);
    console.log(num)

  };
  var min;
  var max;
  useEffect(() => {
    var temp = catFactArray;
    min = 2 + ((page - 1) * 10);
    max = 12 + ((page - 1) * 10);
    var output = temp.slice(min, max);
    output = output.reverse();
    setFacts(output);
  }, [page, catFactArray, num])



  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    if ((num > 1) && (num - 1) % 10 == 0) {
      setPage(page + 1)
    }
  }, [num])


    return (
        <div className = 'App-header'>
          <p className = 'title' >Cat Facts!</p> 
          <p id = 'currentFact'> {facts[facts.length-1]} </p>
          <button onClick={fetchData}>Give me a fact</button>
          <div className = 'pastFacts'> 
            <p> Past Facts!</p>
             {facts.map((facts, index) => (
              <div key={index}>
                  <p>{index + 1 }. {facts}  </p>
              </div>
           ))}
          </div>
          <div>
            <Pagination index = {page} setIndex={setPage} num={num}></Pagination>
          </div>
        </div>
    );
}




export default CatFacts;
