import {useEffect, useState} from 'react';

export default function Fact(props) {
    const { data, facts, setArr } = props;
    const [num, setNum] = useState(1);
    function toggle() {

        let temp = [
            ...facts
        ]
        let index = temp.indexOf(data)
        temp[index] = {
            fact: data.fact,
            i: index
        }
        setNum(num+1);
        setArr(temp)
      }

    return (
            <div onClick={toggle} className="catFact"> 
                <div>
                    <p>{num} {data.fact}</p>    
                    </div>
            </div>
    )
}
