export default function Fact(props) {
    const { data, facts, setArr } = props;
    var index;
    function toggle() {
        let temp = [
            ...facts
        ]
        index = temp.indexOf(data)
        temp[index] = {
            fact: data.fact,
        }
        setArr(temp)
      }

    return (
            <div onClick={toggle} className="catFact"> 
                <div>
                    <p>{index}. {data.fact}</p> 

                    </div>
            </div>
    )
}
